mapper.select:

select rvNum, pdNum, rvText, memNickname
 from
   (
   select /*+ INDEX(review_table PK_REVIEW_RVNUM) */
   rownum rn, rvNum, pdNum, rvText, memNickname
from review_table
where pdNum = #{pdNum}
   and rvNum > 0
   and rownum <= #{cri.pageNum} * #{cri.amount}
) where rn > (#{cri.pageNum} -1) * #{cri.amount}
